using Microsoft.VisualStudio.TestTools.UnitTesting;
using System;
using System.Diagnostics.CodeAnalysis;
using System.Text;

namespace Hestia.Security.Tests.CRYPTO
{
    [TestClass]
    [ExcludeFromCodeCoverage]
    public sealed class RSA_ECB_OAEPWITHSHA1ANDMGF1PADDING
    {
        private const string source = "Hestia.Security";
        private const string pub = "";
        private const string key = "";
        

        [TestMethod]
        public void Test1()
        {
            byte[] output = Security.CRYPTO.RSA_ECB_OAEPWITHSHA1ANDMGF1PADDING_DECRYPT(Convert.FromBase64String(pub), Security.CRYPTO.RSA_ECB_OAEPWITHSHA1ANDMGF1PADDING_ENCRYPT(Convert.FromBase64String(key), Encoding.UTF8.GetBytes(source)));
            Assert.AreEqual(source, Encoding.UTF8.GetString(output));
        }

        [TestMethod]
        public void Test2()
        {
            (byte[] key, byte[] pub) = Security.Utility.RSA_GENKEY();
            byte[] output = Security.CRYPTO.RSA_ECB_OAEPWITHSHA1ANDMGF1PADDING_DECRYPT(pub, Security.CRYPTO.RSA_ECB_OAEPWITHSHA1ANDMGF1PADDING_ENCRYPT(key, Encoding.UTF8.GetBytes(source)));
            Assert.AreEqual(source, Encoding.UTF8.GetString(output));
        }

    }
}